import React, { useEffect, useRef } from 'react';
import { createChart } from 'lightweight-charts';
import './ChartWindow.css';

const ChartWindow = () => {
  const containerRef = useRef();

  useEffect(() => {
    const container = containerRef.current;
    const chart = createChart(container, {
      width: container.clientWidth,
      height: container.clientHeight,
      layout: {
        background: { color: '#1f1f1f' },
        textColor: '#fbf2f2',
      },
      grid: {
        vertLines: { color: '#2a2a2a' },
        horzLines: { color: '#2a2a2a' },
      },
      timeScale: { timeVisible: true, borderColor: '#555' },
    });

    const series = chart.addCandlestickSeries({
      upColor: '#0f0',
      downColor: '#f00',
      wickUpColor: '#0f0',
      wickDownColor: '#f00',
      borderVisible: false,
    });

    series.setData([
      { time: '2024-10-30', open: 180, high: 185, low: 178, close: 183 },
      { time: '2024-10-31', open: 183, high: 188, low: 181, close: 185 },
      { time: '2024-11-01', open: 185, high: 189, low: 183, close: 186 },
    ]);

    // handle window resize
    const handleResize = () => {
      chart.applyOptions({ width: container.clientWidth, height: container.clientHeight });
    };
    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('resize', handleResize);
      chart.remove();
    };
  }, []);

  return (
    <div className="chart-window">
      <div ref={containerRef} className="chart-container" />
    </div>
  );
};

export default ChartWindow;
