import { BrowserRouter as Router, Routes, Route, useNavigate } from "react-router-dom";
import { useEffect } from "react";
import jwtDecode from "jwt-decode";

import Navbar from "./components/Navbar/Navbar";
import ChartWindow from "./components/ChartWindow/ChartWindow";
import Home from "./pages/Home/Home";
import Login from "./pages/Login/Login";
import Signup from "./pages/Signup/Signup";
import About from "./pages/About/About";
import Dashboard from "./pages/Dashboard/Dashboard";
import "./App.css";

// ‚úÖ Helper component to handle token validation and redirect logic
function AuthWatcher() {
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem("token");

    if (token) {
      try {
        const decoded = jwtDecode(token);
        // Check expiration (decoded.exp is in seconds, Date.now() is in ms)
        if (decoded.exp * 1000 < Date.now()) {
          localStorage.removeItem("token");
          navigate("/login");
        }
      } catch (err) {
        // Invalid token ‚Üí clear and redirect
        localStorage.removeItem("token");
        navigate("/login");
      }
    }
  }, [navigate]);

  return null; // this component doesn't render anything visible
}

function App() {
  return (
    <Router>
      <Navbar />
      {/* üîç Token monitor */}
      <AuthWatcher />

      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/login" element={<Login />} />
        <Route path="/about" element={<About />} />
        <Route path="/dashboard" element={<Dashboard />} />
        <Route path="/chart" element={<ChartWindow />} />
        <Route path="/signup" element={<Signup />} />
      </Routes>
    </Router>
  );
}

export default App;
