import { useEffect } from "react";
import "./Home.css";
import { Link } from "react-router-dom";

const Home = () => {
  useEffect(() => {
    document.title = "ChronoView — Home";
  }, []);

  return (
    <div className="home-layout">
      <div className="about-section">
        <h1>Welcome to ChronoView</h1>
        <p>
          ChronoView helps you visualize timelines, manage events, and track key
          moments seamlessly. Whether you're a researcher, student, or just
          someone who loves organizing history — ChronoView gives you the power
          to see time your way.
        </p>
        <p>
          Create an account to save your data or log in to pick up where you
          left off. You can also explore as a guest to preview what’s possible.
        </p>
        <div className="about-buttons">
          <button>Explore as Guest</button>
        </div>
      </div>

      
      <div className="home-login-box">
        <h2>Log in to ChronoView</h2>
        <form className="login-form">
          <input type="email" placeholder="Email address" required />
          <input type="password" placeholder="Password" required />
          <button type="submit">Log In</button>
        </form>
        <p className="login-footer">
          Don’t have an account? <Link to="/signup">Create one</Link>
        </p>
      </div>
    </div>
  );
};

export default Home;
