import React, { useEffect, useRef } from 'react';
import { createChart } from 'lightweight-charts';

const ChartWindow = () => {
  const chartContainer = useRef(null);

  useEffect(() => {
    const chart = createChart(chartContainer.current, {
      width: 1000,
      height: 600,
      layout: { background: { color: '#1f1f1f' }, textColor: '#fbf2f2' },
      grid: {
        vertLines: { color: '#2a2a2a' },
        horzLines: { color: '#2a2a2a' },
      },
      timeScale: { timeVisible: true },
    });

    const candleSeries = chart.addCandlestickSeries({
      upColor: '#00ff88',
      downColor: '#ff0066',
      borderVisible: false,
      wickUpColor: '#00ff88',
      wickDownColor: '#ff0066',
    });

    candleSeries.setData([
      { time: '2024-10-30', open: 180, high: 185, low: 178, close: 183 },
      { time: '2024-10-31', open: 183, high: 188, low: 181, close: 185 },
      { time: '2024-11-01', open: 185, high: 189, low: 183, close: 186 },
    ]);

    return () => chart.remove();
  }, []);

  return <div ref={chartContainer} />;
};

export default ChartWindow;
