import React, { useEffect, useRef } from 'react';
import { createChart } from 'lightweight-charts';
import './ChartWindow.css';

const ChartWindow = () => {
  const chartContainer = useRef(null);

  useEffect(() => {
    const chart = createChart(chartContainer.current, {
      width: 1750,
      height: 800,
      layout: { background: { color: '#1f1f1f' }, textColor: '#fbf2f2' },
      grid: { vertLines: { color: '#2a2a2a' }, horzLines: { color: '#2a2a2a' } },
      timeScale: { timeVisible: true },
    });

    const candleSeries = chart.addCandlestickSeries({
      upColor: '#00ff88',
      downColor: '#ff0066',
      borderVisible: false,
      wickUpColor: '#00ff88',
      wickDownColor: '#ff0066',
    });

    // Load data from backend
    fetch('http://127.0.0.1:8000/api/history?symbol=AAPL')
      .then(res => res.json())
      .then(data => {
        candleSeries.setData(data.candles);

        // optional indicator overlay
        if (data.my_indicator) {
          const indicatorSeries = chart.addLineSeries({
            color: '#ff6600',
            lineWidth: 2,
          });
          indicatorSeries.setData(data.my_indicator);
        }
      });

    return () => chart.remove();
  }, []);

  return (
    <div
      ref={chartContainer}
      style={{
        margin: '50px auto',
        border: '2px solid #ff6600',
        borderRadius: '8px',
        width: '1750px',
        height: '800px',
      }}
    />
  );
};

export default ChartWindow;
